SELECT referer, count(*) as Hits, sip, city, uri, stk
FROM betslip.win:time(7 seconds)
WHERE (uri = "/betslip/WebInterface/BsInterface.aspx?v=277"
OR uri = "/betslip/WebInterface/BsInterface.aspx?v=281"
// uncomment the LIKE commands below to restrict only betslip versions
//OR uri = "/betslip/WebInterface/BsInterface.aspx"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=__"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=1__"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=2__"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=3__"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=40_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=41_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=42_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=43_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=44_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=45_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=46_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=47_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=48_"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=5__"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=6__"
//OR uri LIKE "%/betslip/WebInterface/BsInterface.aspx?v=7__"
)
AND NOT blocked
GROUP BY sip
//HAVING count(*) >1
OUTPUT last every 7 seconds
//

SELECT window(tags) as UQID, userAgent, window(uname) as UserNames, sip, current_timestamp().format() as time, count(distinct referer) as UniqueReferer, sum(CASE WHEN uname REGEXP "^.*[\\W].*[\\W\\S]$" THEN 1 ELSE 0 END) as InvalidUsernames, country, city, window(lok) as LoginStatus
FROM NewLoginDefault.win:time(10 seconds)
WHERE userAgent in (
//'Mozilla/5.0 (iPad; CPU OS 6_0 like Mac OS X) AppleWebKit/536.26 (KHTML, like Gecko) Version/6.0 Mobile/10A5376e Safari/8536.25', //added 13th July 2017. Removed 6th Feb
//'Mozilla/5.0 (iPhone; CPU iPhone OS 8_4_1 like Mac OS X) AppleWebKit/600.1.4 (KHTML, like Gecko) GSA/8.0.57838 Mobile/12H321 Safari/600.1.4', //added 10th August 2017. Removed 6th Feb
//'Mozilla/5.0 (X11; U; Linux x86_64; en-US) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/30.0.1599.114 Safari/537.36 Puffin/4.5.0IT', // added 16th August 2017. Removed 6th Feb
'Mozilla/5.0 (masking-agent; rv:52.0) Gecko/20100101 Firefox/52.0' // added 9th October 2017
// add more user agents here
)
GROUP BY userAgent, sip
OUTPUT last every 10 seconds

SELECT userAgent, sip, current_timestamp().format() as time, count(distinct referer) as UniqueReferer, window(stk) as stk, country, city, uri
FROM MembersAuthenticated.win:time(10 seconds)
WHERE userAgent in (
//'Mozilla/5.0 (iPad; CPU OS 6_0 like Mac OS X) AppleWebKit/536.26 (KHTML, like Gecko) Version/6.0 Mobile/10A5376e Safari/8536.25', //added 13th July 2017. Removed 6th Feb
//'Mozilla/5.0 (iPhone; CPU iPhone OS 8_4_1 like Mac OS X) AppleWebKit/600.1.4 (KHTML, like Gecko) GSA/8.0.57838 Mobile/12H321 Safari/600.1.4', //added 10th August 2017. Removed 6th Feb
//'Mozilla/5.0 (X11; U; Linux x86_64; en-US) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/30.0.1599.114 Safari/537.36 Puffin/4.5.0IT', // added 16th August 2017. Removed 6th Feb
'Mozilla/5.0 (masking-agent; rv:52.0) Gecko/20100101 Firefox/52.0' // added 9th October 2017
// add more user agents here
)
GROUP BY userAgent, sip
OUTPUT last every 10 seconds

SELECT count(*) as hits, userAgent, sip, country, city, referer, serverHost, orgName, current_timestamp().format() as time, stk, uri
FROM MembersAuthenticated.win:time(10 seconds)
WHERE  userAgent IN (
'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/61.0.3163.100 Safari/537.36',
'Mozilla/5.0 (Windows NT 6.1; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/61.0.3163.100 Safari/537.36',
'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.81 Safari/537.36',
'Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/61.0.3163.100 Safari/537.36',
'Mozilla/5.0 (Windows NT 6.3; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/61.0.3163.100 Safari/537.36'
)
//AND serverName = 'mobile.bet365.com'
//AND serverHost = 'mobile.bet365.com'
//AND not blocked
//AND loginDomain = 'SportsBook'
AND referer IN ('https://mobile.bet365.com/',
'https://mobile.bet365.com/Default.aspx?ot=1&amp;apptype=&amp;appversion=')
//AND AuthenticationMethod = 0
//AND tags = 'NoUqid'
//AND uri = 'https://mobile.bet365.com/members/lp/default.aspx'
//AND lok = 'Login Ok'
GROUP BY stk, uri
//HAVING count(distinct stk) = count(distinct uname)
//AND count(distinct stk) > 1
OUTPUT last every 10 seconds

SELECT count(*) as numHits, sip, sldName, uri, userAgent, serverHost, orgName, carrierName, city, country, xForwardedFor, count(distinct uri) as uniqueURI
FROM sportsBook.win:time(10 seconds)
WHERE stk = 'NoSessionToken' 
AND referer <> ''
AND (
// There are lots of bots using mobile URLs on sportsbook
              uri like '/sport/coupon/partial/PartialDefault.aspx%' 
        OR uri like '/V6/sport/coupon/coupon.aspx%'
        OR uri like '/mobilepreload/%'
        OR uri like '/premium/sport/coupon/default.aspx'
// These are the desktop odds requests
       OR uri LIKE '/SportsBook.API/web?%pd=%'        // Added by John Snow - new Sportsbook URLs May 2017
)
AND NOT blocked
GROUP BY sip
HAVING count(*) > 12
//changed to greater than 12 times in 10 seconds by John Snow 20 Feb 2018
//changed to greater than 3 times in 10 seconds by John Snow 27th Nov 2017
//changed to greater than 4 times in 10 seconds by John Snow 15 Feb 2018
OUTPUT last every 10 seconds
